import {createGlobalStyle} from 'styled-components';

export const  GloabalStyle = createGlobalStyle`
body {
    font-family: 'Open Sans Condensed', sans-serif;
    padding: 20px 60px;

    @media
  }


  a {
    text-decoration: none;
    color: black;
  }

  * {
    box-sizing: border-box;
  }
`;
