import React from 'react';
import { ErrorImageOverlay, ErrorImageContainer, ErrorImageText } from './index.styles';

class ErrorBoundary extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            hasErrored: false
        }
    }

    static getDerivedStateFromError(error) {
        // process the error
        return {
            hasErrored: true
        }
    }

    componentDidCatch(error, info) {
        console.log(error, "error");
        console.log(log, "log");
    }

    render(){
        if(this.state.hasErrored){
            return <ErrorImageOverlay>

                <ErrorImageContainer imageUrl = "https://i.igmur.com/yW "/>
                <ErrorImageText>

                </ErrorImageText>
            </ErrorImageOverlay>
        }

        return this.props.children
    }
}

export default ErrorBoundary;
